import * as api from "../utils/api";
import {fetchDecks} from './actionCreators';

export function getAllDecks() {
    return dispatch => {
        try {
            dispatch(fetchDecks([], "loading"));

            api.getDecks()
            .then(decks => {
                dispatch(fetchDecks(decks, "loaded"));
            })
            .catch (() => {
                dispatch(fetchDecks([], "failed"));
            })
        }
        catch (e) {console.log(e)}
    };
}
