import React from 'react';
import {connect} from 'react-redux';
import viewDeckList from '../screens/deckList';

const DeckList = ({decks, navigation}) => {

    const start = 0;
    const count = Math.floor(decks.length / 2);
    const decksGroup1 = decks;
    const decksGroup2 = decksGroup1.splice(start, count);

    return (
        viewDeckList(decksGroup1, decksGroup2, navigation)
    )
}

const mapStateToProps = state => {
    return {
      decks: state.decks.payload.data
    }
}

export default connect(mapStateToProps)(DeckList);
