import React from 'react';
import { StyleSheet, View } from 'react-native';
import * as colors from '../utils/colors';
import {  Button, Text } from 'native-base';

const viewDeckDetails = (deck, handleAddCardBtn, handleQuizBtn) => {

    return (
        <View>
            <View style={styles.textContainer}>
                <Text style={styles.deckName}> {deck.title} </Text>
                <Text style={styles.deckCount}> {deck.questions.length} cards </Text>
            </View>

            <View style={styles.btnContainer}>
                <Button block blue style={styles.buttons} key={deck.id} onPress={handleAddCardBtn} >
                    <Text> Add Card </Text>
                </Button>
                <Button block dark style={styles.buttons} key={deck.id} onPress={handleQuizBtn} >
                    <Text> Start Quiz </Text>
                </Button>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    textContainer: {
        height: "70%",
    },
    btnContainer: {
        height: "30%",
    },
    header: {
        color: "red"
    },
    buttons: {
        margin: 15,
        fontSize: 92,
        height: 50,
        fontWeight: '600',
        textAlign: 'center',
    },
    deckName: {
        marginVertical: 30,
        fontSize: 50,
        color: colors.deckName,
        marginTop: "40%",
        alignSelf: "center"
    },
    deckCount: {
        color: colors.deckCount,
        fontSize: 24,
        alignSelf: "center"
    },
})

export default viewDeckDetails;
